function sizeCaptions(){$(".pointer").each(function(){var e=$(this).closest("li"),t={width:e.width(),height:e.outerHeight()},n=$(this).closest(".caption"),r=$(this).closest("li").find(".projectTitle").width(),i,s=t.width-r;s>100?i=s:i=150;$(n).width(i);var o=$(this).outerHeight();$(this).css("margin-top",t.height/2-o/2)});$(window).width()>699?$("#left").css("min-height",$("#right").height()+"px"):$("#left").css("min-height",0)}$("ul li").hover(function(){$(this).find(".caption").addClass("showCaption")},function(){$(this).find(".caption").removeClass("showCaption")});$("#tags ul li").hover(function(){$(this).find("div").addClass("tagOver")},function(){$(this).find("div").removeClass("tagOver")});sizeCaptions();$(window).resize(function(){sizeCaptions()});var tag_list=$(".detail .tags li").text(),tags=tag_list.split(",");$(".detail .tags li").remove();for(tag in tags)$(".detail .tags").append("<li>"+tags[tag]+"</li>");